import React from "react";
import {Tabs, message, List, Typography, Button, Form, DatePicker,
    InputNumber, Card, Carousel, Image, Modal,} from "antd";

import { bookStay, cancelReservation, getReservations, searchStays,} from "../utils";
import { StayDetailInfoButton } from "../components/HostHomePage";
import { LeftCircleFilled, RightCircleFilled } from "@ant-design/icons";

const { Text } = Typography;
const { TabPane } = Tabs;

class SearchStays extends React.Component {
    state = {
        data: [],
        loading: false,
    };


}

class MyReservations extends React.Component {
    state = {
        loading: false,
        data: [],
    }

    componentDidMount() {
        this.loadData();
    }

    loadData = async() => {
        this.setState({
            loading: true,
        });

        try {
            const resp = await getReservations();
        } catch (error) {
            message.error(error.message());
        } finally {
            this.setState({
                loading: false
            });
        }
    };

    render() {
        return (
            <List
                style={{ width: 1000, margin: "auto" }}
                loading={this.state.loading}
                dataSource={this.state.data}
                renderItem={(item) => (
                    <List.Item
                        //cancel reservation button
                        actions={[]}
                    >
                        <List.Item.Meta
                            title={<Text>{item.stay.name}</Text>}
                            description={
                                <>
                                    <Text>Checkin Date: {item.checkin_date}</Text>
                                    <br />
                                    <Text>Checkout Date: {item.checkout_date}</Text>
                                </>
                            }
                        />
                    </List.Item>
                )}
            />
        );
    }

}

class GuestHomePage extends React.Component {
    render() {
        return (
            <Tabs defaultActiveKey="1" destroyInactiveTabPane={true}>
                <TabPane tab="Search Stays" key="1">
                    Search Stays Content
                </TabPane>
                <TabPane tab="My Reservations" key="2">
                    <MyReservations />
                </TabPane>
            </Tabs>
        );
    }
}

export default GuestHomePage;
