import React from "react";
import {Tabs, message, List, Typography, Button, Form, DatePicker,
    InputNumber, Card, Carousel, Image, Modal,} from "antd";

import { bookStay, cancelReservation, getReservations, searchStays,} from "../utils";
import { StayDetailInfoButton } from "../components/HostHomePage";
import { LeftCircleFilled, RightCircleFilled } from "@ant-design/icons";

const { Text } = Typography;
const { TabPane } = Tabs;

class BookStayButton extends React.Component {
    state = {
      loading: false,
        modalVisible: false,
    };

    handelCancel = () => {
        this.setState({
            modalVisible: false,
        })
    }

    handleSubmit = async (values) => {
        const { stay } = this.props;
        this.setState({
            loading: true,
        });

        try {
            await bookStay({
                checkin_date: values.checkin_date.format("YYYY-MM-DD"),
                checkout_date: values.checkout_date.format("YYYY-MM-DD"),
                stay: {
                    id: stay.id,
                },
            });
            message.success("Successfully book stay");
        } catch (error) {
            message.error(error.message);
        } finally {
            this.setState({
                loading: false,
            });
        }
    };

    render() {
        const { stay } = this.props;
        return(
            <>
                <Button onClick={this.handleBookStay} shape="round" type="primary">
                    Book Stay
                </Button>
                <Modal
                    destroyOnClose={true}
                    title={stay.name}
                    visible={this.state.modalVisible}
                    footer={null}
                    onCancel={this.handleCancel}
                >
                    <Form
                        preserve={false}
                        labelCol={{ span: 8 }}
                        wrapperCol={{ span: 16 }}
                        onFinish={this.handleSubmit}
                    >
                        <Form.Item
                            label="Checkin Date"
                            name="checkin_date"
                            rules={[{ required: true }]}
                        >
                            <DatePicker />
                        </Form.Item>
                        <Form.Item
                            label="Checkout Date"
                            name="checkout_date"
                            rules={[{ required: true }]}
                        >
                            <DatePicker />
                        </Form.Item>
                        <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
                            <Button
                                loading={this.state.loading}
                                type="primary"
                                htmlType="submit"
                            >
                                Book
                            </Button>
                        </Form.Item>
                    </Form>
                </Modal>
            </>
        );
    }

}

class SearchStays extends React.Component {
    state = {
        data: [],
        loading: false,
    };

    search = async (query) => {
        this.setState({
            loading: true,
        });

        try {
            const resp = await searchStays(query);
            this.setState({
                data: resp,
            });
        } catch (error) {
            message.error(error.message);
        } finally {
            this.setState({
                loading: false,
            });
        }
    };

    render() {
        return (
            <>
                <Form onFinish={this.search} layout="inline">
                    <Form.Item
                        label="Guest Number"
                        name="guest_number"
                        rules={[{ required: true }]}
                    >
                        <InputNumber min={1} />
                    </Form.Item>
                    <Form.Item
                        label="Checkin Date"
                        name="checkin_date"
                        rules={[{ required: true }]}
                    >
                        <DatePicker />
                    </Form.Item>

                    <Form.Item
                        label="Checkout Date"
                        name="checkout_date"
                        rules={[{ required: true }]}
                    >
                        <DatePicker />
                    </Form.Item>

                    <Form.Item>
                        <Button
                            loading={this.state.loading}
                            type="primary"
                            htmlType="submit"
                        >
                            Submit
                        </Button>

                    </Form.Item>
                </Form>

                {/*展示用户所选中的日期内的酒店*/}
                <List
                    style={{ marginTop: 20 }}
                    loading={this.state.loading}
                    grid={{
                        gutter: 16,
                        xs: 1,
                        sm: 3,
                        md: 3,
                        lg: 3,
                        xl: 4,
                        xxl: 4,
                    }}
                    dataSource={this.state.data}

                    renderItem={(item) => (
                        <List.Item>
                            <Card
                                key={item.id}
                                title={
                                    <div style={{ display: "flex",
                                        alignItems: "center" }}>
                                        <Text ellipsis={true}
                                              style={{ maxWidth: 150 }}>
                                            {item.name}
                                        </Text>
                                        <StayDetailInfoButton stay={item} />
                                    </div>
                                }
                                extra={<BookStayButton />}
                            >
                                {
                                    <Carousel
                                        dots={false}
                                        arrows={true}
                                        prevArrow={<LeftCircleFilled />}
                                        nextArrow={<RightCircleFilled />}
                                    >
                                        {item.images.map((image, index) => (
                                            <div key={index}>
                                                <Image src={image.url} width="100%" />
                                            </div>
                                        ))}
                                    </Carousel>
                                }
                            </Card>
                        </List.Item>
                    )}
                />
            </>
        );
    }
}

class MyReservations extends React.Component {
    state = {
        loading: false,
        data: [],
    };


    componentDidMount() {
        this.loadData();
    }

    loadData = async() => {
        this.setState({
            loading: true,
        });

        try {
            const resp = await getReservations();
        } catch (error) {
            message.error(error.message());
        } finally {
            this.setState({
                loading: false
            });
        }
    };

    render() {
        return (
            <List
                style={{ width: 1000, margin: "auto" }}
                loading={this.state.loading}
                dataSource={this.state.data}
                renderItem={(item) => (
                    <List.Item
                        //cancel reservation button
                        actions={[]}
                    >
                        <List.Item.Meta
                            title={<Text>{item.stay.name}</Text>}
                            description={
                                <>
                                    <Text>Checkin Date: {item.checkin_date}</Text>
                                    <br />
                                    <Text>Checkout Date: {item.checkout_date}</Text>
                                </>
                            }
                        />
                    </List.Item>
                )}
            />
        );
    }
}


class GuestHomePage extends React.Component {
    render(){
        return (
            <Tabs defaultActiveKey="1" destroyInactiveTabPane={true}>
                <TabPane tab="Search Stays" key="1">
                    <SearchStays />
                </TabPane>
                <TabPane tab="My Reservations" key="2">
                    <MyReservations />
                </TabPane>
            </Tabs>
        );
    }
}

export default GuestHomePage;
