import React from "react";
import {
    Tabs,
    List,
    Card,
    message,
    Typography,
    Button,
    Tooltip,
    Modal,
    Image,
    Space,
    Carousel,
} from "antd";
import {getStaysByHost} from "../utils";
const { TabPane } = Tabs;
const { Text } = Typography;

class MyStays extends React.Component {
    state = {

    };

    componentDidMount() {
        this.loadDate();

    }

    //控制ui的 loading indication的显示
    loadDate = async () => {
        this.setState({
            loading: true,
            });
        try {
            const resp = await getStaysByHost(this.props.stayId);
            this.setState({
                reservations: resp,
            });
        } catch (error) {
            message.error(error.message);
        } finally {
            this.setState({
                loading: false,
            });
        }
    };

    // grid = {{}}是一种props
    // 凡是props不是string的，需要插入第一层{}表示不需要翻译， 而第二层{}则是js object
    render() {
        return (
            <List
    grid={{
        gutter: 16,
        xs: 1,
        sm: 3,
        md: 3,
        lg: 3,
        xl: 4,
        xxl: 4,
    }}
    loading={this.state.load}
    dataSource={this.state.data}
    renderItem={(item) => (
        <List.Item>
            <Card
                key={item.id}
                title={
                    <div style={{display: "flex", alignItems: "center"}}>
                        <Text ellipsis={true} style={{maxWidth: 150}}>
                            {item.name}
                        </Text>
                    </div>
                }
                actions={[]}

            >
                {
                    <Carousel
                        dots={false}
                        arrows={true}
                        prevArrow={<LeftCircleFilled/>}
                        nextArrow={<RightCircleFilled/>}
                    >
                        {item.images.map((image, index) => (
                            <div key={index}>
                                <Image src={image.url} width="100%"/>
                            </div>
                        ))}
                    </Carousel>
                }
            </Card>
        </List.Item>
    )}
    />
        );
    }

}
class HostHomePage extends React.Component {
    //destroyInactiveTabPane: delete inactive TabPlane on the DOM tree

    render() {
        return (
            <Tabs defaultActiveKey="1" destroyInactiveTabPane={true}>
                <TabPane tab="My Stays" key="1">
                    <div>My Stays Content</div>
                </TabPane>
                <TabPane tab="Upload Stay" key="2">
                    <div>Upload Stays</div>
                </TabPane>
            </Tabs>
        );
    }
}

export default HostHomePage;
