import React from "react";
import {
    Tabs,
    List,
    Card,
    message,
    Typography,
    Button,
    Tooltip,
    Modal,
    Image,
    Space,
    Carousel,
} from "antd";
import {getStaysByHost} from "../utils";
import {
    InfoCircleOutlined,
    LeftCircleFilled,
    RightCircleFilled,
} from "@ant-design/icons";
const { TabPane } = Tabs;
const { Text } = Typography;

class MyStays extends React.Component {
    state = {
        loading: false,
        data: [],
    };

    componentDidMount() {
        this.loadDate();
    }

    //控制ui的 loading indication的显示
    loadDate = async () => {
        this.setState({
            loading: true,
            });
        try {
            const resp = await getStaysByHost(this.props.stayId);
            this.setState({
                reservations: resp,
            });
        } catch (error) {
            message.error(error.message);
        } finally {
            this.setState({
                loading: false,
            });
        }
    };

    // grid = {{}}是一种props
    // 凡是props不是string的，需要插入第一层{}表示不需要翻译， 而第二层{}则是js object
    render() {
        const { loading, reservations } = this.state;
        return (
            <List
                loading={loading}
                dataSource={reservations}
                renderItem={(item) => (
                    <List.Item>
                        <List.Item.Meta
                            title={<Text>Guest Name: {item.guest.username}</Text>}
                            description={
                                <>
                                    <Text>Checkin Date: {item.checkin_date}</Text>
                                    <br />
                                    <Text>Checkout Date: {item.checkout_date}</Text>
                                </>
                            }
                        />
                    </List.Item>
                )}
            />
        );
    }
}

class HostHomePage extends React.Component {
    //destroyInactiveTabPane: delete inactive TabPlane on the DOM tree

    render() {
        return (
            <Tabs defaultActiveKey="1" destroyInactiveTabPane={true}>
                <TabPane tab="My Stays" key="1">
                    <div>My Stays Content</div>
                </TabPane>
                <TabPane tab="Upload Stay" key="2">
                    <div>Upload Stays</div>
                </TabPane>
            </Tabs>
        );
    }
}

export default HostHomePage;
