import React from "react";
import {
    Tabs,
    List,
    Card,
    message,
    Typography,
    Button,
    Tooltip,
    Modal,
    Image,
    Space,
    Carousel,
} from "antd";
import {getStaysByHost} from "../utils";
import {
    InfoCircleOutlined,
    LeftCircleFilled,
    RightCircleFilled,
} from "@ant-design/icons";
const { TabPane } = Tabs;
const { Text } = Typography;

export class StayDetailInfoButton extends React.Component {
    state = {
        modalVisible: false,
    };

    openModal = () => {
        this.setState({
            modalVisible: true,
        });
    };

    //控制modal的状态
    handleCancel = () => {
        this.setState({
            modalVisible: false,
        });
    };

    render() {
        const { stay } = this.props;
        const { name, description, address, guest_number } = stay;
        const { modalVisible } = this.state;
        //name of 弹窗: modal
        return (
            <>
                <Tooltip title="View Stay Details">
                    <Button
                        onClick={this.openModal}
                        style={{ border: "none" }}
                        size="large"
                        icon={<InfoCircleOutlined />}
                    />
                </Tooltip>
                {modalVisible && (
                    <Modal
                        title={name}
                        centered={true}
                        visible={modalVisible}
                        closable={false}
                        footer={null}
                        onCancel={this.handleCancel}
                    >
                        <Space direction="vertical">
                            <Text strong={true}>Description</Text>
                            <Text type="secondary">{description}</Text>
                            <Text strong={true}>Address</Text>
                            <Text type="secondary">{address}</Text>
                            <Text strong={true}>Guest Number</Text>
                            <Text type="secondary">{guest_number}</Text>
                        </Space>
                    </Modal>
                )}
            </>
        );
    }
}

class MyStays extends React.Component {
    state = {
        loading: false,
        data: [],
    };

    componentDidMount() {
        this.loadData();
    }

    //控制ui的 loading indication的显示
    loadData = async () => {
        this.setState({
            loading: true,
        });

        try {
            const resp = await getStaysByHost();
            this.setState({
                data: resp,
            });
        } catch (error) {
            message.error(error.message);
        } finally {
            this.setState({
                loading: false,
            });
        }
    };

    // grid = {{}}是一种props
    // 凡是props不是string的，需要插入第一层{}表示不需要翻译， 而第二层{}则是js object
    render() {
        return (
            <List
                loading={this.state.loading}
                grid={{
                    gutter: 16,
                    xs: 1,
                    sm: 3,
                    md: 3,
                    lg: 3,
                    xl: 4,
                    xxl: 4,
                }}
                dataSource={this.state.data}
                renderItem={(item) => (
                    <List.Item>
                        <Card
                            key={item.id}
                            title={
                                <div style={{ display: "flex", alignItems: "center" }}>
                                    <Text ellipsis={true} style={{ maxWidth: 150 }}>
                                        {item.name}
                                    </Text>
                                    <StayDetailInfoButton stay={item} />
                                </div>
                            }
                            actions={[]}
                            extra={null}
                        >
                            {
                                <Carousel
                                    dots={false}
                                    arrows={true}
                                    prevArrow={<LeftCircleFilled />}
                                    nextArrow={<RightCircleFilled />}
                                >
                                    {/*{item.images.map((image, index) => (*/}
                                    {/*    <div key={index}>*/}
                                    {/*        <Image src={image.url} width="100%" />*/}
                                    {/*    </div>*/}
                                    {/*))}*/}
                                </Carousel>
                            }
                        </Card>
                    </List.Item>
                )}
            />
        );
    }
}

class HostHomePage extends React.Component {
    //destroyInactiveTabPane: delete inactive TabPlane on the DOM tree

    render() {
        return (
            <Tabs defaultActiveKey="1" destroyInactiveTabPane={true}>
                <TabPane tab="My Stays" key="1">
                    <div>My Stays Content</div>
                    <MyStays />
                </TabPane>
                <TabPane tab="Upload Stay" key="2">
                    <div>Upload Stays</div>
                </TabPane>
            </Tabs>
        );
    }
}

export default HostHomePage;
