import { Layout, Dropdown, Menu, Button } from "antd";
import { UserOutlined } from "@ant-design/icons";
import React from "react";
import LoginPage from "./components/LoginPage";
import HostHomePage from "./components/HostHomePage";

const { Header, Content } = Layout;
//deconstruction，等效于 const Header = Layout.Haader

class App extends React.Component {
  state = {
    authed: false,
    asHost: false,
  };

  //coponentDidMount：app component的render函数执行完之后（一来到主页时），所做的事情
  componentDidMount() {
    //log-in persistance
    // localStorage: 格式-map，可以persist数据
    const authToken = localStorage.getItem("authToken");
    const asHost = localStorage.getItem("asHost") === "true";
    this.setState({
      authed: authToken !== null,
      asHost,
    });
  }

  handleLoginSuccess = (token, asHost) => {
    localStorage.setItem("authToken", token);
    localStorage.setItem("asHost", asHost);
    this.setState({
      authed: true,
      asHost,
    });
  };

  handleLogOut = () => {
    localStorage.removeItem("authToken");
    localStorage.removeItem("asHost");
    this.setState({
      authed: false,
    });
  };

  //在外层定义content， 用于处理当content的逻辑复杂时，专门针对content的render函数
  renderContent = () => {

    if (!this.state.authed) {
      return <LoginPage handleLoginSuccess={this.handleLoginSuccess} />;
    }

    if (this.state.asHost) {
      return <HostHomePage />;
    }

    return <div>guest home page</div>;
  };

  userMenu = (
      <Menu>
        <Menu.Item key="logout" onClick={this.handleLogOut}>
          Log Out
        </Menu.Item>
      </Menu>
  );

  render() {
    return (
        <Layout style={{ height: "100vh" }}>
          <Header style={{ display: "flex", justifyContent: "space-between" }}>
            <div style={{ fontSize: 16, fontWeight: 600, color: "white" }}>
              Stays Booking
            </div>
            {this.state.authed && (
                <div>
                  <Dropdown trigger="click" overlay={this.userMenu}>
                    <Button icon={<UserOutlined />} shape="circle" />
                  </Dropdown>
                </div>
            )}
          </Header>

          <Content
              style={{ height: "calc(100% - 64px)", margin: 20, overflow: "auto" }}
          >
            {this.renderContent()}
          </Content>
        </Layout>
    );
  }
}

export default App;
